                isLoading: false,
                categoryList: [],
                categoryIdMap: {},
                handleTitle: '',
                form: {},
                searchType: '',
                search_key_word: '',
                showType: 'normalData',
                cityList: [],
                stateList: ['商家同意退款', '用户已投诉', '交易成功（待评价）', '交易成功（已点评）', '交易失败（已取消）', '待退款', '退款成功']
            };
        },
        methods: {
            handleSub(row){
                var state = row.state
                if(state == 1){
                     this.$confirm('是否确认退款', '提示', {
                        confirmButtonText: '确定',
                        cancelButtonText: '取消',
                        type: 'warning'
                        }).then(() => {
                        this.$message({
                            type: 'success',
                            message: '退款成功!'
                        });
                        }).catch(() => {
                        this.$message({
                            type: 'info',
                            message: '已取消退款'
                        });          
                    });
                }
                if(state == 2)
                    this.showRemark = true
            },
            handleSensitive(number) {
                return number.replace(number.substring(3, 7), '****');
            },
