                        this.$message({
                            type: 'info',
                            message: action === 'cancel'
                                ? '拒绝退款'
                                : '关闭页面'
                        })
                    });
            },
            getShowState(e) {
                if (parseInt(e) === 0) {
                    this.isLoading = true;
                    this.getTableData();
                    this.isLoading = false;
                } else {
                    api.searchSpecifiedOrder({
                        attrName: 'state',
                        attrValue: e,
                        pageNum: this.query.pageIndex,
                        pageSize: this.query.pageSize
                    }).then(res => {
                        this.tableData = res.data.orders;
                        this.pageTotal = res.data.total;
