                var cl = [];
                cityList.forEach(c => {
                    var t = {
                        value: c.id,
                        label: c.city
                    };
                    cl.push(t);
                });
                return cl;
            },
            pageInit() {
                api.getAllCities().then(res => {
                    if (res.code === 200) {
                        this.cityList = this.initCity(res.data);
                        localStorage.setItem('cityList', JSON.stringify(this.cityList));
                    }
                }).catch(err => {
                    console.log(err);
                }).then(res => {
                    this.getTableData();
                });
            }
        },
        created() {
            this.pageInit();
        }
    };
</script>
