/**
* @Description: 订单显示
* @Author: TankNee
* @Date: 2/24/2020 7:45 PM
**/
<template>
    <div>
        <div class="crumbs">
            <el-breadcrumb>
                <el-breadcrumb-item>
                    <i class="el-icon-files"></i> 常量列表
                </el-breadcrumb-item>
            </el-breadcrumb>
        </div>
        <div class="container">
            <div class="handle-box">
                <el-select v-model="show_state" placeholder="显示全部" class="handle-select mr10" @change="getShowState">
                    <el-option key="0" label="显示全部" value="0"></el-option>
                    <el-option key="1" label="待付款" value="1"></el-option>
                    <el-option key="2" label="待出行" value="2"></el-option>
                    <el-option key="3" label="交易成功(未点评)" value="3"></el-option>
                    <el-option key="4" label="交易成功(已点评)" value="4"></el-option>
                    <el-option key="5" label="交易失败(已取消)" value="5"></el-option>
                    <el-option key="6" label="待退款" value="6"></el-option>
                    <el-option key="7" label="退款成功" value="7"></el-option>
                </el-select>
                <el-input style="margin-left: 30px" placeholder="请输入内容" v-model="search_key_word"
                          class="input-with-select" clearable>
                    <el-select style="width: 110px;" v-model="searchType" slot="prepend"
                               placeholder="下单用户" @change="getSearchType">
                        <el-option label="下单用户" value="0"></el-option>
                        <el-option label="产品名称" value="1"></el-option>
                        <el-option label="下单时间" value="2"></el-option>
                        <el-option label="处理时间" value="3"></el-option>
                        <el-option label="联系人" value="4"></el-option>
                        <el-option label="出行人" value="5"></el-option>
                    </el-select>
                    <el-button slot="append" icon="el-icon-search" @click="getSearchResult"></el-button>
                </el-input>
            </div>
            <div>
                <el-table
                        :data="tableData"
                        v-loading="isLoading"
                        border

                        ref="multipleTable"
                        @select="handleSelection"
                        @select-all="handleSelection"
                >
                    <!-- <el-table-column type="selection" width="60" align="center"></el-table-column> -->
                    <el-table-column prop="user_nickname" label="用户名" width="90" align="center"></el-table-column>
                    <el-table-column prop="title" label="产品名称" width="240" align="center"></el-table-column>
                    <el-table-column prop="adult_num" label="成人"  align="center"></el-table-column>
                    <el-table-column prop="child_num" label="儿童"  align="center"></el-table-column>
                    <el-table-column prop="order_time" label="下单时间" width="150" align="center">
                        <template slot-scope="scope">
                            {{new Date(scope.row.order_time).toLocaleString()}}
                        </template>
                    </el-table-column>
                    <el-table-column prop="link_man" label="联系人" width="140" align="center">
                        <template slot-scope="scope">
                            <ul>
                                <li v-for="linkMan in scope.row.link_man" :key="linkMan" style="list-style: none">
                                    {{linkMan.name}}
                                    {{handleSensitive(linkMan.mobile)}}
                                </li>
                            </ul>
                        </template>
                    </el-table-column>
                    <el-table-column prop="passenger_ids" label="出行人" width="140" align="center">
                        <template slot-scope="scope">
                            <ul>
                                <li v-for="pa in scope.row.passenger" :key="pa" style="list-style: none">
                                    {{pa.name}}{{handleSensitive(pa.id_card)}}
                                </li>
                            </ul>
                        </template>
                    </el-table-column>
                    <el-table-column prop="state" label="订单状态" width="150" align="center">
                        <template slot-scope="scope">
                            <el-tag
                                    :type="scope.row.state === 6?'primary':'success'"
                                    :style="scope.row.state === 6?'cursor: pointer':''"
                                    @click="handleRefund(scope.row)"
                            >{{handleTags(scope.row)}}
                            </el-tag>
                        </template>
                    </el-table-column>
                    <el-table-column label="操作" align="center" width="160">
                        <template slot-scope="scope" >
                            <el-button type="primary" v-if="scope.row.state == 2 || scope.row.state == 1"
                            @click="handleSub(scope.row)">{{scope.row.state == 1?'确认退款':scope.row.state == 2?'查看投诉内容':''}}</el-button>
                        </template>
                    </el-table-column>
                </el-table>
                <el-dialog title="投诉内容" :visible="showRemark">
                    XXXXXXXXXXXXXXXXXXX
                      <div slot="footer" class="dialog-footer">
                            <el-button >确认撤单</el-button>
                            <el-button type="primary" @click="dialogFormVisible = false">确 定</el-button>
                        </div>
                </el-dialog>
                <div class="pagination">
                    <el-pagination
                            background
                            layout="total, prev, pager, next"
                            :current-page="query.pageIndex"
                            :page-size="query.pageSize"
                            :total="pageTotal"
                            @current-change="handlePageChange"
                    ></el-pagination>
                </div>
            </div>
        </div>
    </div>
</template>
<script>
    import api from '../../utils/api';

    export default {
        name: 'OrderList',
        data() {
            return {
                showRemark:false,
                query: {
                    address: '',
                    name: '',
                    pageIndex: 1,
                    pageSize: 10
                },
                show_state: '',
                tableData: [
                    {
                        user_nickname: "TY",
                        adult_num: 1,
                        child_num: 1,
                        departure_time: "2020-04-23",
                        id: 1,
                        link_man: [
                            {   mobile: "13476818230",name: "TY"    }
                        ],
                        passenger:[
                            {   mobile: "13476818230",name: "TY"    }
                        ],
                        order_time: "2020-05-23T01:52:39.000+0000",
                        state: 3,
                        title: "三亚春节热售 豪品0自费 印象丽江+洱海大邮轮+玉龙雪山"
                    }
                ],
                pageTotal: 10,
                isLoading: false,
                categoryList: [],
                categoryIdMap: {},
                handleTitle: '',
                form: {},
                searchType: '',
                search_key_word: '',
                showType: 'normalData',
                cityList: [],
                stateList: ['商家同意退款', '用户已投诉', '交易成功（待评价）', '交易成功（已点评）', '交易失败（已取消）', '待退款', '退款成功']
            };
        },
        methods: {
            handleSub(row){
                var state = row.state
                if(state == 1){
                     this.$confirm('是否确认退款', '提示', {
                        confirmButtonText: '确定',
                        cancelButtonText: '取消',
                        type: 'warning'
                        }).then(() => {
                        this.$message({
                            type: 'success',
                            message: '退款成功!'
                        });
                        }).catch(() => {
                        this.$message({
                            type: 'info',
                            message: '已取消退款'
                        });          
                    });
                }
                if(state == 2)
                    this.showRemark = true
            },
            handleSensitive(number) {
                return number.replace(number.substring(3, 7), '****');
            },
            handleTags(data) {
                return this.stateList[data.state - 1];
            },
            handleRefund(data) {
                if (data.state !== 6) {
                    return;
                }
                this.$confirm('是否接受退款申请', '提示', {
                    distinguishCancelAndClose: true,
                    type: 'warning',
                    confirmButtonText: '接受',
                    cancelButtonText: '拒绝'
                })
                    .then(() => {

                    })
                    .catch(action => {
                        this.$message({
                            type: 'info',
                            message: action === 'cancel'
                                ? '拒绝退款'
                                : '关闭页面'
                        })
                    });
            },
            getShowState(e) {
                if (parseInt(e) === 0) {
                    this.isLoading = true;
                    this.getTableData();
                    this.isLoading = false;
                } else {
                    api.searchSpecifiedOrder({
                        attrName: 'state',
                        attrValue: e,
                        pageNum: this.query.pageIndex,
                        pageSize: this.query.pageSize
                    }).then(res => {
                        this.tableData = res.data.orders;
                        this.pageTotal = res.data.total;
                        this.isLoading = false;
                    });
                }

            },
            getTableData() {
                this.isLoading = true;
                var that = this;
                api.getOrderList({
                    pageNum: that.query.pageIndex,
                    pageSize: that.query.pageSize
                }).then(res0 => {
                    this.tableData = res0.data.orders;
                    this.pageTotal = res0.data.total;
                    this.isLoading = false;
                    console.log(this.form);
                }).catch(err => {
                    console.log(err);
                });
            },
            handleSelection(e) {
                console.log(e);
            },
            handlePageChange(val, e) {
                this.$set(this.query, 'pageIndex', val);
                if (this.showType === 'normalData') {
                    this.getTableData();
                } else {
                    this.getSearchResult(e, val);
                }

            },
            getSearchType(e) {

            },
            getSearchResult(e, pageNum = this.query.pageIndex) {
                this.isLoading = true;
                var list = ['user_nickname', 'title', 'order_time', 'deal_time', 'linkMan', 'passenger'];
                var that = this;
                this.query.pageIndex = 1;
                if (this.search_key_word) {
                    this.showType = 'searching';
                    api.searchSpecifiedOrder({
                        attrName: list[this.searchType] || 'user_nickname',
                        attrValue: this.search_key_word,
                        pageNum: pageNum,
                        pageSize: that.query.pageSize
                    }).then(res => {
                        this.tableData = res.data.orders;
                        this.pageTotal = res.data.total;
                        this.isLoading = false;
                    });

                } else {
                    this.showType = 'normal';
                    this.$message.error('请输入搜索内容');
                    this.query.pageIndex = 1;
                    this.getTableData();
                    this.isLoading = false;
                }
                console.log(this.showType);

            },
            //数组去重
            uniArr(arr) {
                var obj = {};
                var new_arr = arr.reduce(function(prev, element) {
                    if (!prev.find(el => el.category_con_id == element.category_con_id)) {
                        prev.push(element);
                    }
                    return prev;
                }, []);
                return new_arr;
            },
            //初始化城市列表
            initCity(cityList) {
                var cl = [];
                cityList.forEach(c => {
                    var t = {
                        value: c.id,
                        label: c.city
                    };
                    cl.push(t);
                });
                return cl;
            },
            pageInit() {
                api.getAllCities().then(res => {
                    if (res.code === 200) {
                        this.cityList = this.initCity(res.data);
                        localStorage.setItem('cityList', JSON.stringify(this.cityList));
                    }
                }).catch(err => {
                    console.log(err);
                }).then(res => {
                    this.getTableData();
                });
            }
        },
        created() {
            this.pageInit();
        }
    };
</script>
<style scoped>
    .handle-box {
        margin-bottom: 20px;
        display: flex;
    }

    .dialog-footer {
        right: 30px;
    }
</style>
